{
	"Version": "2012-10-17",
	"Statement": [
		{
			"Effect": "Allow",
			"Action": [
				"ec2:DescribeVolumes",
				"ec2:DescribeSnapshots"
			],
			"Resource": "*"
		},
		{
			"Effect": "Allow",
			"Action": [
				"ec2:CreateSnapshot"
			],
			"Resource": [
				"arn:aws:ec2:*:*:snapshot/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Image Builder"
				}
			}
		},
		{
			"Effect": "Allow",
			"Action": [
				"ec2:CreateSnapshot"
			],
			"Resource": [
				"arn:aws:ec2:*:*:volume/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Image Builder"
				}
			}
		},
		{
			"Effect": "Allow",
			"Action": [
				"ec2:CreateTags"
			],
			"Resource": "*",
			"Condition": {
				"StringEquals": {
					"ec2:CreateAction": "CreateSnapshot",
					"aws:RequestTag/CreatedBy": [
						"EC2 Image Builder"
					]
				}
			}
		},
		{
			"Effect": "Allow",
			"Action": [
				"s3:GetObject"
			],
			"Resource": [
				"arn:aws:s3:::*/*.ISO",
				"arn:aws:s3:::*/*.iso",
				"arn:aws:s3:::*/*.Iso"
			],
			"Condition": {
				"StringEquals": {
					"s3:ResourceAccount": "${aws:PrincipalAccount}"
				}
			}
		},
		{
			"Effect": "Allow",
			"Action": [
				"imagebuilder:GetComponent",
				"imagebuilder:GetMarketplaceResource"
			],
			"Resource": "*"
		},
		{
			"Effect": "Allow",
			"Action": [
				"kms:Decrypt"
			],
			"Resource": "*",
			"Condition": {
				"ForAnyValue:StringEquals": {
					"kms:EncryptionContextKeys": "aws:imagebuilder:arn",
					"aws:CalledVia": [
						"imagebuilder.amazonaws.com"
					]
				}
			}
		},
		{
			"Effect": "Allow",
			"Action": [
				"s3:GetObject"
			],
			"Resource": "arn:aws:s3:::ec2imagebuilder*"
		},
		{
			"Effect": "Allow",
			"Action": [
				"logs:CreateLogStream",
				"logs:CreateLogGroup",
				"logs:PutLogEvents"
			],
			"Resource": "arn:aws:logs:*:*:log-group:/aws/imagebuilder/*"
		}
	]
}

{
	"Version": "2012-10-17",
	"Statement": [
		{
			"Sid": "AllowRunInstances",
			"Effect": "Allow",
			"Action": [
				"ec2:RunInstances"
			],
			"Resource": [
				"arn:aws:ec2:*:*:subnet/*",
				"arn:aws:ec2:*:*:network-interface/*",
				"arn:aws:ec2:*::image/*",
				"arn:aws:ec2:*:*:key-pair/*",
				"arn:aws:ec2:*:*:security-group/*",
				"arn:aws:ec2:*:*:launch-template/*",
				"arn:aws:license-manager:*:*:license-configuration:*"
			]
		},
		{
			"Sid": "AllowRunInstancesOnFastLaunchCreatedResources",
			"Effect": "Allow",
			"Action": [
				"ec2:RunInstances"
			],
			"Resource": [
				"arn:aws:ec2:*:*:volume/*",
				"arn:aws:ec2:*:*:instance/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowPassRole",
			"Effect": "Allow",
			"Action": "iam:PassRole",
			"Resource": "*",
			"Condition": {
				"StringEquals": {
					"iam:PassedToService": [
						"ec2.amazonaws.com",
						"ec2.amazonaws.com.cn"
					]
				}
			}
		},
		{
			"Sid": "AllowStopAndTerminateInstances",
			"Effect": "Allow",
			"Action": [
				"ec2:StopInstances",
				"ec2:TerminateInstances"
			],
			"Resource": [
				"arn:aws:ec2:*:*:instance/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowCreateSnapshot",
			"Effect": "Allow",
			"Action": "ec2:CreateSnapshot",
			"Resource": [
				"arn:aws:ec2:*:*:volume/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowCreateTaggedSnapshot",
			"Effect": "Allow",
			"Action": "ec2:CreateSnapshot",
			"Resource": [
				"arn:aws:ec2:*:*:snapshot/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"StringLike": {
					"aws:RequestTag/CreatedByLaunchTemplateVersion": "*"
				},
				"ForAnyValue:StringEquals": {
					"aws:TagKeys": [
						"CreatedByLaunchTemplateName",
						"CreatedByLaunchTemplateId"
					]
				}
			}
		},
		{
			"Sid": "AllowCreateLaunchTemplate",
			"Effect": "Allow",
			"Action": "ec2:CreateLaunchTemplate",
			"Resource": "arn:aws:ec2:*:*:launch-template/*",
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowCreateTags",
			"Effect": "Allow",
			"Action": "ec2:CreateTags",
			"Resource": [
				"arn:aws:ec2:*:*:volume/*",
				"arn:aws:ec2:*:*:instance/*",
				"arn:aws:ec2:*:*:snapshot/*",
				"arn:aws:ec2:*:*:launch-template/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:CreateAction": [
						"CreateSnapshot",
						"RunInstances",
						"CreateLaunchTemplate"
					]
				}
			}
		},
		{
			"Sid": "AllowDeleteSnapshots",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteSnapshot"
			],
			"Resource": [
				"arn:aws:ec2:*:*:snapshot/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowDeleteVolumes",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteVolume"
			],
			"Resource": [
				"arn:aws:ec2:*:*:volume/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowDeleteNetworkInterfaces",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteNetworkInterface"
			],
			"Resource": [
				"arn:aws:ec2:*:*:network-interface/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "AllowDescribeActions",
			"Effect": "Allow",
			"Action": [
				"ec2:DescribeImages",
				"ec2:DescribeSnapshots",
				"ec2:DescribeSubnets",
				"ec2:DescribeInstanceAttribute",
				"ec2:DescribeInstanceStatus",
				"ec2:DescribeInstances",
				"ec2:DescribeInstanceTypeOfferings",
				"ec2:DescribeLaunchTemplateVersions",
				"ec2:DescribeLaunchTemplates",
				"ec2:DescribeVolumes",
				"ec2:DescribeNetworkInterfaces",
				"cloudformation:DescribeStacks"
			],
			"Resource": "*"
		},
		{
			"Sid": "AllowPutMetricData",
			"Effect": "Allow",
			"Action": "cloudwatch:PutMetricData",
			"Resource": "*",
			"Condition": {
				"StringEquals": {
					"cloudwatch:namespace": "AWS/EC2"
				}
			}
		},
		{
			"Sid": "AllowEventsRuleMutations",
			"Effect": "Allow",
			"Action": [
				"events:DeleteRule",
				"events:RemoveTargets",
				"events:PutRule",
				"events:PutTargets"
			],
			"Condition": {
				"StringEquals": {
					"events:ManagedBy": "ec2fastlaunch.amazonaws.com"
				}
			},
			"Resource": [
				"arn:aws:events:*:*:rule/FastLaunch*"
			]
		},
		{
			"Sid": "AllowEventsRuleNonMutations",
			"Effect": "Allow",
			"Action": [
				"events:ListTargetsByRule",
				"events:DescribeRule"
			],
			"Resource": [
				"arn:aws:events:*:*:rule/FastLaunch*"
			]
		},
		{
			"Sid": "AllowKMSActions",
			"Effect": "Allow",
			"Action": "kms:ListRetirableGrants",
			"Resource": "*"
		},
		{
			"Sid": "AllowDeleteFastLaunchLaunchTemplates",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteLaunchTemplate"
			],
			"Resource": "*",
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		}
	]
}

{
	"Version": "2012-10-17",
	"Statement": [
		{
			"Sid": "Ec2ImagePermission",
			"Effect": "Allow",
			"Action": [
				"ec2:EnableImage",
				"ec2:DeregisterImage",
				"ec2:EnableImageDeprecation",
				"ec2:DescribeImageAttribute",
				"ec2:DisableImage",
				"ec2:DisableImageDeprecation"
			],
			"Resource": "arn:aws:ec2:*::image/*",
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Image Builder"
				}
			}
		},
		{
			"Sid": "EC2DeleteSnapshotPermission",
			"Effect": "Allow",
			"Action": "ec2:DeleteSnapshot",
			"Resource": "arn:aws:ec2:*::snapshot/*",
			"Condition": {
				"StringEquals": {
					"aws:ResourceTag/CreatedBy": "EC2 Image Builder"
				}
			}
		},
		{
			"Sid": "EC2TagsPermission",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteTags",
				"ec2:CreateTags"
			],
			"Resource": [
				"arn:aws:ec2:*::snapshot/*",
				"arn:aws:ec2:*::image/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/DeprecatedBy": "EC2 Image Builder",
					"aws:ResourceTag/CreatedBy": "EC2 Image Builder"
				},
				"ForAllValues:StringEquals": {
					"aws:TagKeys": "DeprecatedBy"
				}
			}
		},
		{
			"Sid": "ECRImagePermission",
			"Effect": "Allow",
			"Action": [
				"ecr:BatchGetImage",
				"ecr:BatchDeleteImage"
			],
			"Resource": "arn:aws:ecr:*:*:repository/*",
			"Condition": {
				"StringEquals": {
					"ecr:ResourceTag/LifecycleExecutionAccess": "EC2 Image Builder"
				}
			}
		},
		{
			"Sid": "ImageBuilderEC2TagServicePermission",
			"Effect": "Allow",
			"Action": [
				"ec2:DescribeImages",
				"tag:GetResources",
				"imagebuilder:DeleteImage"
			],
			"Resource": "*"
		}
	]
}

{
	"Version": "2012-10-17",
	"Statement": [
		{
			"Sid": "EC2FastLaunch",
			"Effect": "Allow",
			"Action": [
				"ec2:EnableFastLaunch",
				"ec2:DisableFastLaunch",
				"ec2:DescribeFastLaunchImages"
			],
			"Resource": "*"
		},
		{
			"Sid": "EC2ReadOnly",
			"Effect": "Allow",
			"Action": [
				"ec2:DescribeImages",
				"ec2:DescribeLaunchTemplateVersions",
				"ec2:DescribeSnapshots",
				"ec2:DescribeVolumes",
				"ec2:DescribeRegions",
				"ec2:DescribeSecurityGroups",
				"ec2:DescribeSubnets",
				"ec2:DescribeVpcs",
				"ec2:DescribeInstances",
				"ec2:DescribeLaunchTemplates",
				"ec2:DescribeTags",
				"ec2:DescribeAvailabilityZones",
				"ec2:DescribeAccountAttributes"
			],
			"Resource": "*"
		},
		{
			"Sid": "EC2CreateVPC",
			"Effect": "Allow",
			"Action": [
				"ec2:CreateVpc"
			],
			"Resource": [
				"arn:aws:ec2:*:*:vpc/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2ModifyDeleteVPC",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteVpc",
				"ec2:CreateSubnet",
				"ec2:ModifyVpcAttribute",
				"ec2:CreateSecurityGroup"
			],
			"Resource": [
				"arn:aws:ec2:*:*:vpc/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:ResourceTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2CreateSubnet",
			"Effect": "Allow",
			"Action": [
				"ec2:CreateSubnet"
			],
			"Resource": [
				"arn:aws:ec2:*:*:subnet/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2DeleteSubnet",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteSubnet"
			],
			"Resource": [
				"arn:aws:ec2:*:*:subnet/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:ResourceTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2CreateSecurityGroup",
			"Effect": "Allow",
			"Action": [
				"ec2:CreateSecurityGroup"
			],
			"Resource": [
				"arn:aws:ec2:*:*:security-group/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2ManageSecurityGroupEgress",
			"Effect": "Allow",
			"Action": [
				"ec2:AuthorizeSecurityGroupEgress",
				"ec2:RevokeSecurityGroupEgress"
			],
			"Resource": [
				"arn:aws:ec2:*:*:security-group/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:ResourceTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2DeleteSecurityGroup",
			"Effect": "Allow",
			"Action": [
				"ec2:DeleteSecurityGroup"
			],
			"Resource": [
				"arn:aws:ec2:*:*:security-group/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:ResourceTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "CloudFormation",
			"Effect": "Allow",
			"Action": [
				"cloudformation:DescribeStacks",
				"cloudformation:CreateStack",
				"cloudformation:UpdateStack",
				"cloudformation:RollbackStack",
				"cloudformation:DeleteStack",
				"cloudformation:UpdateTerminationProtection",
				"cloudformation:DescribeStackEvents",
				"cloudformation:DescribeStackResource",
				"cloudformation:DescribeStackResources"
			],
			"Resource": [
				"arn:aws:cloudformation:*:*:stack/EC2FastLaunch*/*"
			],
			"Condition": {
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2LaunchTemplateModify",
			"Effect": "Allow",
			"Action": [
				"ec2:ModifyLaunchTemplate",
				"ec2:CreateLaunchTemplate",
				"ec2:CreateLaunchTemplateVersion"
			],
			"Resource": "arn:aws:ec2:*:*:launch-template/*",
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "EC2LaunchInstance",
			"Effect": "Allow",
			"Action": [
				"ec2:RunInstances"
			],
			"Resource": [
				"arn:aws:ec2:*:*:subnet/*",
				"arn:aws:ec2:*:*:network-interface/*",
				"arn:aws:ec2:*::image/*",
				"arn:aws:ec2:*:*:key-pair/*",
				"arn:aws:ec2:*:*:security-group/*",
				"arn:aws:ec2:*:*:launch-template/*",
				"arn:aws:license-manager:*:*:license-configuration:*"
			]
		},
		{
			"Sid": "EC2LaunchInstanceWithVolAndInstance",
			"Effect": "Allow",
			"Action": [
				"ec2:RunInstances"
			],
			"Resource": [
				"arn:aws:ec2:*:*:volume/*",
				"arn:aws:ec2:*:*:instance/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				}
			}
		},
		{
			"Sid": "EC2Tags",
			"Effect": "Allow",
			"Action": "ec2:CreateTags",
			"Resource": [
				"arn:aws:ec2:*:*:volume/*",
				"arn:aws:ec2:*:*:instance/*",
				"arn:aws:ec2:*:*:snapshot/*"
			],
			"Condition": {
				"StringEquals": {
					"ec2:CreateAction": "RunInstances"
				}
			}
		},
		{
			"Sid": "EC2ManageTags",
			"Effect": "Allow",
			"Action": "ec2:CreateTags",
			"Resource": [
				"arn:aws:ec2:*:*:security-group/*",
				"arn:aws:ec2:*:*:launch-template/*",
				"arn:aws:ec2:*:*:vpc/*",
				"arn:aws:ec2:*:*:subnet/*"
			],
			"Condition": {
				"StringEquals": {
					"aws:RequestTag/CreatedBy": "EC2 Fast Launch"
				},
				"ForAnyValue:StringLike": {
					"aws:CalledVia": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "IAMSLR",
			"Effect": "Allow",
			"Action": "iam:CreateServiceLinkedRole",
			"Resource": "arn:aws:iam::*:role/aws-service-role/ec2fastlaunch.amazonaws.com/AWSServiceRoleForEC2FastLaunch",
			"Condition": {
				"StringLike": {
					"iam:AWSServiceName": "ec2fastlaunch.amazonaws.com"
				}
			}
		},
		{
			"Sid": "IAMSLRPassRole",
			"Effect": "Allow",
			"Action": "iam:PassRole",
			"Resource": [
				"arn:aws:iam::*:instance-profile/*",
				"arn:aws:iam::*:role/*"
			],
			"Condition": {
				"StringEquals": {
					"iam:PassedToService": [
						"ec2.amazonaws.com",
						"ec2.amazonaws.com.cn"
					]
				}
			}
		}
	]
}
